'use client';
import { useMachine, normalizeProps } from '@zag-js/react';
import * as select from '@zag-js/select';
import { useId } from 'react';
import { createSplitProps } from '../create-split-props.mjs';
import '../environment/index.mjs';
import { useEnvironmentContext } from '../environment/environment-context.mjs';

const useSelect = (props) => {
  const [collectionOptions, rest] = createSplitProps()(props, [
    "isItemDisabled",
    "itemToValue",
    "itemToString",
    "items"
  ]);
  const collection = select.collection(collectionOptions);
  const getRootNode = useEnvironmentContext();
  const initialContext = {
    id: useId(),
    getRootNode,
    collection,
    ...rest,
    value: props.defaultValue
  };
  const context = {
    ...initialContext,
    collection,
    value: props.value
  };
  const [state, send] = useMachine(select.machine(initialContext), {
    context
  });
  return select.connect(state, send, normalizeProps);
};

export { useSelect };
