// src/logic.ts
var match = (...fns) => (v) => {
  for (let fn of fns) {
    let x = fn(v);
    if (x != null)
      return x;
  }
};
function when(predicate, fn) {
  return (v) => {
    if (predicate(v))
      return fn(v);
  };
}
var otherwise = (fn) => (v) => when(() => !0, fn)(v), orElse = (u) => (v) => (
  // @ts-ignore
  v ?? (typeof u == "function" ? u() : u)
);

export {
  match,
  when,
  otherwise,
  orElse
};
